import React, { useState, useEffect } from "react";

import axios from "axios";

import { Button, Form, Container, Modal } from "react-bootstrap";

const Entries = () => {
  const [entries, setEntries] = useState([]);
  const [refreshData, setRefreshData] = useState(false);
  const [changeEntry, setChangeEntry] = useState({ change: false, id: 0 });
  const [changeIngredient, setChangeIngredient] = useState({
    change: false,
    id: 0,
  });
  const [newIngredientName, setNewIngredientName] = useState("");
  const [addNewEntry, setAddNewEntry] = useState(false);
  const [newEntry, setNewEntry] = useState({
    stockid: "",
    name: "",
    price: "",
    company: "",
  });

  useEffect(() => {
    getAllEntries();
  }, []);

  if (refreshData) {
    setRefreshData(false);
    getAllEntries();
  }

  return (
    <div className="p-5">
      <div class="overflow-x-auto relative">
        <table class="w-full text-sm text-left text-gray-500 dark:text-gray-400">
          <thead class="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
            <tr>
              <th scope="col" class="py-3 px-6">
                Product name
              </th>
              <th scope="col" class="py-3 px-6">
                Color
              </th>
              <th scope="col" class="py-3 px-6">
                Category
              </th>
              <th scope="col" class="py-3 px-6">
                Price
              </th>
            </tr>
          </thead>
          <tbody>
            <tr class="bg-white border-b dark:bg-gray-800 dark:border-gray-700">
              <th
                scope="row"
                class="py-4 px-6 font-medium text-gray-900 whitespace-nowrap dark:text-white"
              >
                Apple MacBook Pro 17"
              </th>
              <td class="py-4 px-6">Sliver</td>
              <td class="py-4 px-6">Laptop</td>
              <td class="py-4 px-6">$2999</td>
            </tr>
            <tr class="bg-white border-b dark:bg-gray-800 dark:border-gray-700">
              <th
                scope="row"
                class="py-4 px-6 font-medium text-gray-900 whitespace-nowrap dark:text-white"
              >
                Microsoft Surface Pro
              </th>
              <td class="py-4 px-6">White</td>
              <td class="py-4 px-6">Laptop PC</td>
              <td class="py-4 px-6">$1999</td>
            </tr>
          </tbody>
        </table>
        <Container>
          <button
            className="bg-blue-700 rounded-full p-2 text-white"
            onClick={() => setAddNewEntry(true)}
          >
            Track today's calories
          </button>
        </Container>

        <Container>
          {entries != null &&
            entries.map((entry, i) => <div>{entry.stockid}</div>)}
        </Container>

        <Modal show={addNewEntry} onHide={() => setAddNewEntry(false)} centered>
          <Modal.Header closeButton>
            <Modal.Title>Add Calorie Entry</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form.Group>
              <Form.Label>Dish</Form.Label>
              <Form.Control
                onChange={(event) => {
                  newEntry.dish = event.target.value;
                }}
              ></Form.Control>
              <Form.Label>Ingredients</Form.Label>
              <Form.Control
                onChange={(event) => {
                  newEntry.ingredients = event.target.value;
                }}
              ></Form.Control>
              <Form.Label>Calories</Form.Label>
              <Form.Control
                onChange={(event) => {
                  newEntry.calories = event.target.value;
                }}
              ></Form.Control>
              <Form.Label>Fat</Form.Label>
              <Form.Control
                type="number"
                onChange={(event) => {
                  newEntry.fat = event.target.value;
                }}
              ></Form.Control>
            </Form.Group>
            <Button onClick={() => addSingleEntry()}>Add</Button>
            <Button onClick={() => setAddNewEntry(false)}>Cancel</Button>
          </Modal.Body>
        </Modal>

        <Modal
          show={changeIngredient.change}
          onHide={() => setChangeIngredient({ change: false, id: 0 })}
          centered
        ></Modal>
        <Modal.Header closeButton>
          <Modal.Title>Change Ingredients</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>New Ingredients</Form.Label>
            <Form.Control
              onChange={(event) => {
                setNewIngredientName(event.target.value);
              }}
            ></Form.Control>
          </Form.Group>
          <Button onClick={() => changeIngredientForEntry()}>Change</Button>
          <Button onClick={() => setChangeIngredient({ change: false, id: 0 })}>
            Cancel
          </Button>
        </Modal.Body>

        <Modal
          show={changeEntry.change}
          onHide={() => setChangeEntry({ change: false, id: 0 })}
          centered
        >
          <Modal.Header closeButton>
            <Modal.Title>Change Entry</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form.Group>
              <Form.Label>Dish</Form.Label>
              <Form.Control
                onChange={(event) => {
                  newEntry.dish = event.target.value;
                }}
              ></Form.Control>
              <Form.Label>Ingredients</Form.Label>
              <Form.Control
                onChange={(event) => {
                  newEntry.ingredients = event.target.value;
                }}
              ></Form.Control>
              <Form.Label>Calories</Form.Label>
              <Form.Control
                onChange={(event) => {
                  newEntry.calories = event.target.value;
                }}
              ></Form.Control>
              <Form.Label>Fat</Form.Label>
              <Form.Control
                type="number"
                onChange={(event) => {
                  newEntry.fat = event.target.value;
                }}
              ></Form.Control>
            </Form.Group>
            <Button onClick={() => changeSingleEntry()}>Change</Button>
            <Button onClick={() => setChangeEntry({ change: false, id: 0 })}>
              Cancel
            </Button>
          </Modal.Body>
        </Modal>
      </div>
    </div>
  );

  function changeIngredientForEntry() {
    changeIngredient.change = false;
    var url = "http://localhost:8080/ingredient/update/" + changeIngredient.id;
    axios
      .put(url, {
        ingredients: newIngredientName,
      })
      .then((response) => {
        console.log(response);
        if (response.status === 200) {
          setRefreshData(true);
        }
      });
  }

  function changeSingleEntry() {
    changeEntry.change = false;
    var url = "http://localhost:8000/entry/update/" + changeEntry.id;
    axios.put(url, newEntry).then((response) => {
      if (response.status === 200) {
        setRefreshData(true);
      }
    });
  }

  function addSingleEntry() {
    setAddNewEntry(false);
    var url = "http://localhost:8000/entry/create";
    axios
      .post(url, {
        ingredients: newEntry.ingredients,
        dish: newEntry.dish,
        calories: newEntry.calories,
        fat: parseFloat(newEntry.fat),
      })
      .then((response) => {
        if (response.status === 200) {
          setRefreshData(true);
        }
      });
  }

  function deleteSingleEntry(id) {
    var url = "http://localhost:8000/entry/delete" + id;
    axios.delete(url, {}).then((response) => {
      if (response.status === 200) {
        setRefreshData(true);
      }
    });
  }

  function getAllEntries() {
    var url = "http://localhost:8080/entries";
    axios
      .get(url, {
        responseType: "json",
      })
      .then((response) => {
        if (response.status === 200) {
          setEntries(response.data);
        }
      });
  }
};

export default Entries;
